import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import ProgressBar from '../pages/ProgressBar';

import '../css/SecondPage.css';

const options = [
  { name: 'Learning specific skill to Advanced My Career', img: 'data:image/png;base64,' },
  { name: 'Exploring new Topics I am interested in', img: 'data:image/jpeg;base64,' },
  { name: 'Refreshing my math foundation', img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSqrRE-stLpppjIb28lFuS8gRCwFtH44y7Xr46ZLT2EDA_oWNhoMXZ1QEt350sBhgrMPRU&usqp=CAU' },
  { name: 'Expering my brain to stay sharp', img: 'data:image/jpeg;base64,' },
  { name: 'Something Else', img: 'data:image/png;base64,' },
  
];

function SecondPage() {
  const [selectedOption, setSelectedOption] = useState(null);
  const [progress, setProgress] = useState(28.5714);
  const navigate = useNavigate();

  const handleOptionClick = (option) => {
    setSelectedOption(option.name);
  };

  const handleContinueClick = () => {
    setProgress(progress + 25);
    navigate('/third');
  };

  const handleBackClick = () => {
    navigate(-1);
  };

  return (
    <div className="SecondPage">
      <button className="backButton" onClick={handleBackClick}>ðŸ ”</button> {/* Add class to the back button */}
      <ProgressBar progress={progress} />
      <h1>Which Describe You Best ?</h1>
      <h3>This Will help us Personalised Your Experience</h3>
      
      <div className="options">
        {options.map((option, index) => (
          <div
            key={index}
            className={`option ${selectedOption === option.name ? 'selected' : ''}`}
            onClick={() => handleOptionClick(option)}
          >
            <img src={option.img} alt="" />
            <p>{option.name}</p>
          </div>
        ))}
      </div>

      <button disabled={!selectedOption} onClick={handleContinueClick}>Continue</button>
    </div>
  );
}

export default SecondPage;
